CREATE EXTERNAL TABLE gold.trip_data_green_2020_01 WITH ( LOCATION='gold/trip_data_green/year=2020/month=01', DATA_SOURCE = nyc_taxi_src, FILE_FORMAT = parquet_file_format ) AS select td.year, td.month, cast(td.lpep_pickup_datetime as date) as trip_date, tz.borough, cal.day_name as pickup_day, CASE WHEN upper(cal.day_name) in ('SATURDAY','SUNDAY') THEN 'Y' else 'N' END AS trip_day_weekend_ind, SUM(CASE WHEN upper(pt.payment_type_desc)='CREDIT CARD' THEN 1 ELSE 0 END) AS card_trip_count, SUM(CASE WHEN upper(pt.payment_type_desc)='CASH' THEN 1 ELSE 0 END) AS cash_trip_count FROM silver.vw_trip_data_green as td JOIN silver.taxi_zone tz ON td.pu_location_id=tz.location_id JOIN silver.calendar cal ON cast(td.lpep_pickup_datetime as date)=cal.date JOIN silver.payment_type pt ON td.payment_type=pt.payment_type where td.year='2020' and td.month='01' GROUP BY td.year, td.month, cast(td.lpep_pickup_datetime as date), tz.borough, cal.day_name
